// üîç NODO 3: B√öSQUEDA EN PINECONE
// Este nodo busca contenido relevante en la base de datos vectorial usando el embedding

// Obtener datos del nodo anterior
const { pregunta, embedding } = $input.first().json;

console.log('üîç Iniciando b√∫squeda en Pinecone para:', pregunta);

// Configuraci√≥n de Pinecone
const pineconeApiKey = $env.PINECONE_API_KEY;
const pineconeEnvironment = $env.PINECONE_ENVIRONMENT;
const pineconeIndexName = $env.PINECONE_INDEX_NAME;

if (!pineconeApiKey || !pineconeEnvironment || !pineconeIndexName) {
  throw new Error('‚ùå Variables de Pinecone no configuradas: PINECONE_API_KEY, PINECONE_ENVIRONMENT, PINECONE_INDEX_NAME');
}

// Construir URL de Pinecone
const pineconeUrl = `https://${pineconeIndexName}-${pineconeEnvironment}.svc.pinecone.io/query`;

// Funci√≥n para buscar en Pinecone
async function buscarEnPinecone(queryEmbedding, topK = 5) {
  const response = await $http.request({
    method: 'POST',
    url: pineconeUrl,
    headers: {
      'Api-Key': pineconeApiKey,
      'Content-Type': 'application/json'
    },
    body: {
      vector: queryEmbedding,
      topK: topK,
      includeMetadata: true,
      includeValues: false
    }
  });

  if (response.status !== 200) {
    throw new Error(`‚ùå Error en Pinecone API: ${response.status} - ${response.statusText}`);
  }

  return response.data;
}

// Realizar b√∫squeda
const resultadosBusqueda = await buscarEnPinecone(embedding, 5);

// Extraer contexto relevante
const matches = resultadosBusqueda.matches || [];
const contexto = matches
  .filter(match => match.score > 0.7) // Solo matches con alta similitud
  .map(match => match.metadata?.text || '')
  .filter(text => text.length > 0)
  .join('\n\n');

console.log('üìã Contexto encontrado:');
console.log(`   - Matches encontrados: ${matches.length}`);
console.log(`   - Matches relevantes: ${matches.filter(m => m.score > 0.7).length}`);
console.log(`   - Longitud del contexto: ${contexto.length} caracteres`);

if (contexto.length === 0) {
  console.log('‚ö†Ô∏è  No se encontr√≥ contexto relevante');
}

// Devolver datos para el siguiente nodo
return {
  pregunta: pregunta,
  contexto: contexto,
  matches: matches,
  numeroResultados: matches.length,
  tieneContexto: contexto.length > 0
};

// ü§ñ NODO 4: GENERAR RESPUESTA CON GOOGLE GEMINI
// Este nodo construye el prompt final y genera la respuesta usando Gemini

// Obtener datos del nodo anterior
const { pregunta, contexto, tieneContexto, numeroResultados } = $input.first().json;

console.log('ü§ñ Generando respuesta para:', pregunta);
console.log('üìö Contexto disponible:', tieneContexto);

// Configuraci√≥n de Google Gemini
const googleApiKey = $env.GOOGLE_API_KEY;
if (!googleApiKey) {
  throw new Error('‚ùå GOOGLE_API_KEY no est√° configurada');
}

// Construir el prompt seg√∫n si hay contexto o no
let promptFinal;

if (tieneContexto) {
  promptFinal = `Eres un asistente jur√≠dico especializado en el C√≥digo Penal espa√±ol. Responde bas√°ndote √∫nicamente en el contexto proporcionado.

CONTEXTO DEL C√ìDIGO PENAL:
${contexto}

PREGUNTA:
${pregunta}

INSTRUCCIONES:
- Responde bas√°ndote √∫nicamente en el contexto del C√≥digo Penal proporcionado
- Si la informaci√≥n no est√° en el contexto, ind√≠calo claramente
- Cita los art√≠culos espec√≠ficos cuando sea posible
- Usa un lenguaje claro y profesional
- Si es relevante, menciona las penas asociadas

RESPUESTA:`;
} else {
  promptFinal = `Eres un asistente jur√≠dico especializado en el C√≥digo Penal espa√±ol.

PREGUNTA:
${pregunta}

No se encontr√≥ informaci√≥n espec√≠fica en el C√≥digo Penal para responder a esta pregunta.

Responde educadamente explicando que:
1. No se encontr√≥ informaci√≥n relevante en el C√≥digo Penal procesado
2. Sugiere reformular la pregunta usando t√©rminos jur√≠dicos m√°s espec√≠ficos
3. Recuerda que solo puedes consultar sobre el C√≥digo Penal espa√±ol

RESPUESTA:`;
}

// Funci√≥n para llamar a Google Gemini
async function generarRespuestaGemini(prompt) {
  const response = await $http.request({
    method: 'POST',
    url: `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash:generateContent?key=${googleApiKey}`,
    headers: {
      'Content-Type': 'application/json'
    },
    body: {
      contents: [{
        parts: [{
          text: prompt
        }]
      }],
      generationConfig: {
        temperature: 0.3,
        topK: 20,
        topP: 0.8,
        maxOutputTokens: 800
      }
    }
  });

  if (response.status !== 200) {
    throw new Error(`‚ùå Error en Google Gemini API: ${response.status} - ${response.statusText}`);
  }

  return response.data.candidates[0].content.parts[0].text.trim();
}

// Generar la respuesta
const respuestaFinal = await generarRespuestaGemini(promptFinal);

console.log('‚úÖ Respuesta generada correctamente con Google Gemini');
console.log('üìù Longitud de la respuesta:', respuestaFinal.length, 'caracteres');

// Preparar respuesta final para el webhook
const respuestaCompleta = {
  respuesta: respuestaFinal,
  metadata: {
    pregunta: pregunta,
    tieneContexto: tieneContexto,
    numeroResultados: numeroResultados,
    timestamp: new Date().toISOString(),
    modelo: 'gemini-1.5-flash',
    dominio: 'codigo-penal-espanol'
  }
};

return respuestaCompleta;
